// R√©cup√©ration des donn√©es du deal Pipedrive
const deal = $input.first().json;

// ========================================
// 1Ô∏è‚É£ EXTRACTION DES DONN√âES
// ========================================

// Extraction du num√©ro de t√©l√©phone (plusieurs emplacements possibles dans Pipedrive)
let rawPhone = null;

// Cas 1 : person_id.phone (array d'objets)
if (deal.person_id?.phone && Array.isArray(deal.person_id.phone) && deal.person_id.phone.length > 0) {
  // Prioriser le num√©ro primary, sinon prendre le premier
  const primaryPhone = deal.person_id.phone.find(p => p.primary === true);
  rawPhone = primaryPhone?.value || deal.person_id.phone[0]?.value;
}

// Cas 2 : phone direct (string)
if (!rawPhone && deal.phone) {
  rawPhone = deal.phone;
}

// Cas 3 : person_id.value avec phone
if (!rawPhone && deal.person_id?.value) {
  rawPhone = deal.person_id.value;
}

// Extraction de l'email (plusieurs emplacements possibles)
let email = null;

// Cas 1 : person_id.email (array d'objets)
if (deal.person_id?.email && Array.isArray(deal.person_id.email) && deal.person_id.email.length > 0) {
  const primaryEmail = deal.person_id.email.find(e => e.primary === true);
  email = primaryEmail?.value || deal.person_id.email[0]?.value;
}

// Cas 2 : email direct
if (!email && deal.email) {
  email = deal.email;
}

// ========================================
// 2Ô∏è‚É£ NORMALISATION DU NUM√âRO
// ========================================

/**
 * Normalise un num√©ro fran√ßais vers le format E.164 (+33XXXXXXXXX)
 * G√®re les formats :
 * - 06XXXXXXXX ou 07XXXXXXXX (format local)
 * - +336XXXXXXXX ou +337XXXXXXXX (format international)
 * - 00336XXXXXXXX ou 00337XXXXXXXX (format international alternatif)
 * - 336XXXXXXXX ou 337XXXXXXXX (sans pr√©fixe)
 * - Avec espaces, tirets, points : 06 12 34 56 78, 06-12-34-56-78, etc.
 */
function normalizeFrenchPhone(phone) {
  if (!phone || typeof phone !== 'string') {
    return null;
  }

  // Nettoyer le num√©ro : supprimer tous les caract√®res non num√©riques sauf le +
  let cleaned = phone.trim().replace(/[\s\-\.\(\)]/g, '');

  // Cas 1 : Format +33XXXXXXXXX (d√©j√† bon format)
  if (/^\+33[67]\d{8}$/.test(cleaned)) {
    return cleaned;
  }

  // Cas 2 : Format 0033XXXXXXXXX ‚Üí +33XXXXXXXXX
  if (/^0033[67]\d{8}$/.test(cleaned)) {
    return '+' + cleaned.substring(2);
  }

  // Cas 3 : Format 33XXXXXXXXX ‚Üí +33XXXXXXXXX
  if (/^33[67]\d{8}$/.test(cleaned)) {
    return '+' + cleaned;
  }

  // Cas 4 : Format 0[67]XXXXXXXX ‚Üí +33[67]XXXXXXXX
  if (/^0[67]\d{8}$/.test(cleaned)) {
    return '+33' + cleaned.substring(1);
  }

  // Cas 5 : Format [67]XXXXXXXX (manque les z√©ros) ‚Üí +33[67]XXXXXXXX
  if (/^[67]\d{8}$/.test(cleaned)) {
    return '+33' + cleaned;
  }

  // Si aucun format ne correspond, retourner null
  return null;
}

// Normalisation du num√©ro
const normalizedPhone = rawPhone ? normalizeFrenchPhone(rawPhone) : null;

// ========================================
// 3Ô∏è‚É£ VALIDATION DU NUM√âRO
// ========================================

// V√©rifier que c'est bien un mobile fran√ßais valide (06 ou 07)
const isFrenchMobile = normalizedPhone !== null && /^\+33[67]\d{8}$/.test(normalizedPhone);

// ========================================
// 4Ô∏è‚É£ VALIDATION DE L'EMAIL
// ========================================

// Regex simple mais robuste pour valider l'email
const isValidEmail = email && /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

// ========================================
// 5Ô∏è‚É£ D√âTECTION DU FORMAT ORIGINAL
// ========================================

let originalFormat = 'N/A';
if (rawPhone) {
  if (/^\+33/.test(rawPhone)) {
    originalFormat = '+33 (international)';
  } else if (/^0033/.test(rawPhone)) {
    originalFormat = '0033 (international alt)';
  } else if (/^33/.test(rawPhone)) {
    originalFormat = '33 (sans +)';
  } else if (/^0[67]/.test(rawPhone)) {
    originalFormat = '06/07 (local)';
  } else {
    originalFormat = 'Format inconnu';
  }
}

// ========================================
// 6Ô∏è‚É£ RETOUR DES R√âSULTATS
// ========================================

return {
  // Donn√©es valid√©es et normalis√©es
  phone_normalized: normalizedPhone,
  email: email || null,

  // Flags de validation
  has_valid_french_mobile: isFrenchMobile,
  has_valid_email: isValidEmail,

  // Informations de d√©bogage
  raw_phone: rawPhone || null,
  original_phone_format: originalFormat,

  // Informations du deal (passthrough)
  deal_id: deal.id,
  deal_title: deal.title,
  person_name: deal.person_id?.name || deal.person_name || 'N/A',
  stage_id: deal.stage_id,

  // M√©tadonn√©es pour logging
  validation_timestamp: new Date().toISOString(),

  // IMPORTANT : Conserver toutes les donn√©es originales pour la suite du workflow
  original_deal_data: deal
};

// ========================================
// NODE : Prepare Prospect Data
// ========================================
// R√¥le : Pr√©fixer les IDs + pr√©parer les donn√©es pour insertion
// ========================================

const items = $input.all();
const clientVars = $('Set Client Variables').first().json;

return items.map(item => {
  const aiResponse = item.json.message.content;
  const prospect = aiResponse.prospect;
  const sequence = aiResponse.sequence;
  const aiAnalysis = aiResponse.ai_analysis;

  // ========================================
  // 1. PR√âFIXAGE DES IDs EXTERNES
  // ========================================
  const prefixId = (id) => {
    if (!id) return null;
    // Si d√©j√† pr√©fix√©, ne rien faire
    if (id.includes('_')) return id;
    // Sinon, pr√©fixer avec le client_slug
    return `${clientVars.client_slug}_${id}`;
  };

  const prefixedDealId = prefixId(prospect.external_deal_id);
  const prefixedUserId = prefixId(prospect.external_user_id);

  // ========================================
  // 2. PR√âPARATION DONN√âES PROSPECT
  // ========================================
  const prospectData = {
    deployment_id: clientVars.deployment_id,
    external_source: prospect.external_source,
    external_deal_id: prefixedDealId,
    external_user_id: prefixedUserId,
    first_name: prospect.first_name,
    last_name: prospect.last_name,
    email: prospect.email,
    phone_number: prospect.phone_number,
    company_name: prospect.company_name,
    status: prospect.status,
    ai_analysis: aiAnalysis
  };

  // ========================================
  // 3. PR√âPARATION DONN√âES SEQUENCE
  // ========================================
  const sequenceData = {
    deployment_id: clientVars.deployment_id,
    sequence_number: clientVars.sequence_number,
    max_attempts: clientVars.max_attempts,
    current_attempt: 0,
    status: sequence.status,
    next_action_at: new Date().toISOString() // NOW
  };

  // ========================================
  // 4. PR√âPARATION CONFIG AGENT DIPLER
  // ========================================
  const agentConfig = {
    workspaceId: clientVars.workspace_id_dipler,
    companyId: clientVars.company_id_dipler,
    fromPhoneNumber: clientVars.from_phone_number,
    agentId: clientVars.agent_id_dipler,
    // ‚ö†Ô∏è IMPORTANT : Utiliser l'ID ORIGINAL pour Dipler/Pipedrive
    dealId: prospect.external_deal_id, // Sans pr√©fixe
    personId: prospect.external_user_id // Sans pr√©fixe
  };

  // ========================================
  // 5. RETOUR DES DONN√âES PR√âPAR√âES
  // ========================================
  return {
    json: {
      // Donn√©es pour Supabase (IDs pr√©fix√©s)
      prospect_data: prospectData,
      sequence_data: sequenceData,

      // Donn√©es pour Dipler/Pipedrive (IDs originaux)
      agent_config: agentConfig,

      // Donn√©es brutes pour r√©f√©rence
      ai_analysis: aiAnalysis,
      client_vars: clientVars,

      // IDs originaux et pr√©fix√©s pour debug
      original_deal_id: prospect.external_deal_id,
      original_user_id: prospect.external_user_id,
      prefixed_deal_id: prefixedDealId,
      prefixed_user_id: prefixedUserId
    }
  };
});
